var Lamp = require('./lamp.js'); //TODO: load all objects
var Home = require('./home.js'); //TODO: load all objects

var objects = new Object();
function Objects(){
	
}
Objects.prototype.getObjectById = function(identifier){
	return objects[identifier];
}
Objects.prototype.insert = function(identifier, reference){
	objects[identifier] = reference;
}

Objects.prototype.init = function(){
	this.insert('home', new Home());
	
	this.insert('bedroom', new Lamp('Bedroom RGB Lamp', 'bedroom'));
	this.insert('bathroom', new Lamp('Bathroom Lamp', 'bathroom'));
	this.insert('living', new Lamp('Living RGB Lamp', 'living'));
	this.insert('kitchen', new Lamp('Kitchen Lamp', 'kitchen'));
	this.insert('hol', new Lamp('Hol Lamp', 'hol'));
	

	var initialize = function(selector, context, r) {
	  return objects[selector];
	};
	return initialize;
}
// export the class
module.exports = Objects;