
// Constructor
function Alarm() {
	this.init();
}
Alarm.prototype.init = function(){
	// the person exited from home
	self = this;
	$('home').on('locked', function(message){
		/* set the alarm */
		self.setAlarm();
	});
}
Alarm.prototype.setAlarm = function(){
	console.log('alarm set');
};

// export the class
module.exports = Alarm;

